package com.wgg.essay.service.user.impl;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.wgg.essay.dao.UserMapper;import com.wgg.essay.dto.user.UserDto;import com.wgg.essay.po.User;import com.wgg.essay.service.user.UserService;import com.wgg.essay.utils.Md5Util;@Servicepublic class UserServiceImpl implements UserService{	@Autowired UserMapper userMapper;	@Autowired Md5Util md5Util;		@Override	public Integer login(UserDto dto) {		List<User> users = userMapper.findByAccount(dto.getPo().getAccount());		if(users.size()>0) {			User user = users.get(0);			boolean flag = md5Util.verify(dto.getPo().getPwd(), user.getPwd());			dto.setPo(null);			if(flag) {				return user.getId();			}			return null;		}		dto.setPo(null);		return null;	}	@Override	public Integer reg(UserDto dto) {		List<User> users = userMapper.findByTelephoneOrEmail(dto.getPo());		if(users.size()>0){			return 0;		}		else{			dto.getPo().setPwd(md5Util.generate(dto.getPo().getPwd()));			Integer id = userMapper.reg(dto.getPo());			return id;		}	}	}