package com.wgg.essay.service.user.impl;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.wgg.essay.config.Md5Util;import com.wgg.essay.dao.UserMapper;import com.wgg.essay.dto.user.UserDto;import com.wgg.essay.po.User;import com.wgg.essay.service.user.UserService;@Servicepublic class UserServiceImpl implements UserService{	@Autowired UserMapper userMapper;	@Autowired Md5Util md5Util;		@Override	public boolean login(UserDto dto) {		List<User> users = userMapper.findByAccount(dto.getPo().getAccount());		User user = users.get(0);		boolean flag = md5Util.verify(dto.getPo().getPwd(), user.getPwd());		if(flag) {			dto.setToken("token");		}		return flag;	}	@Override	public Integer reg(UserDto dto) {		dto.getPo().setPwd(md5Util.generate(dto.getPo().getPwd()));		Integer id = userMapper.reg(dto.getPo());		return id;	}	}