package com.wgg.essay.utils;import org.springframework.beans.factory.annotation.Value;import org.springframework.stereotype.Component;@Component("propertiesUtil")public class PropertiesUtil {	@Value("${essay.baseHref}")	private String baseHref;	@Value("${essay.key}")	private String key;	public String getBaseHref() {		return baseHref;	}	public void setBaseHref(String baseHref) {		this.baseHref = baseHref;	}	public String getKey() {		return key;	}	public void setKey(String key) {		this.key = key;	}	}